mapper.select:
select SUM(`premium`)
from cust_warranty_cost LEFT JOIN cust_warranty ON cust_warranty.warranty_uuid=cust_warranty_cost.warranty_uuid

    cust_warranty.state = 1 AND cust_warranty.warranty_status != 1 AND cust_warranty.warranty_status != 6

        and cust_warranty.account_uuid=#{account_uuid}


        and cust_warranty.channel_id=#{channel_id}


        and cust_warranty.manager_uuid=#{manager_uuid}


        and cust_warranty.agent_id=#{agent_id}


        and cust_warranty.start_time>=#{start_time}


        and cust_warranty.start_time<#{end_time}

select from_unixtime(cust_warranty_cost.actual_pay_time/1000,"%Y-%m-%d") `time_text`,sum(`cust_warranty_cost`.`premium`) `premium`,sum(`cust_warranty_cost`.`phase`=1) `insurance_policy_count`
select from_unixtime(cust_warranty_cost.actual_pay_time/1000,"%Y-%m-%d") `time_text`,sum(`cust_warranty_cost`.`premium`) `premium`,sum(`cust_warranty_cost`.`phase`=1) `insurance_policy_count`
select from_unixtime(cust_warranty_cost.actual_pay_time/1000,"%Y") `time_text`,sum(`cust_warranty_cost`.`premium`) `premium`,sum(`cust_warranty_cost`.`phase`=1) `insurance_policy_count`
select from_unixtime(cust_warranty_cost.actual_pay_time/1000,"%Y-%m") `time_text`,sum(`cust_warranty_cost`.`premium`) `premium`,sum(`cust_warranty_cost`.`phase`=1) `insurance_policy_count`
from cust_warranty_cost LEFT JOIN cust_warranty ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid` where `cust_warranty_cost`.`actual_pay_time`>=#{start_time} AND `cust_warranty_cost`.`actual_pay_time`<#{end_time} AND `cust_warranty_cost`.`pay_status`= 206 AND `cust_warranty`.`manager_uuid`=#{manager_uuid}
and `cust_warranty`.`agent_id`=#{agent_id}
group by `time_text`;

select `cust_warranty`.`id` warranty_id,`cust_warranty`.`warranty_uuid`,`cust_warranty`.`warranty_code`, `cust_warranty`.`product_id`,`cust_warranty`.`ins_company_id`,`cust_warranty`.`count`,`cust_warranty`.`pay_category_id`, `cust_warranty`.`warranty_status`,`cust_warranty`.`type`, `cust_warranty_cost`.`phase`,`cust_warranty_cost`.`premium`,`cust_warranty_cost`.`actual_pay_time`, `cust_warranty_brokerage`.`id` brokerage_id,`cust_warranty_brokerage`.`cost_id`,`cust_warranty_brokerage`.`warranty_money`,`cust_warranty_brokerage`.`manager_money`, `cust_warranty_brokerage`.`channel_money`,`cust_warranty_brokerage`.`agent_money`,`cust_warranty_brokerage`.`warranty_rate`,`cust_warranty_brokerage`.`manager_rate`,`cust_warranty_brokerage`.`channel_rate`,`cust_warranty_brokerage`.`agent_rate` from cust_warranty_cost LEFT JOIN cust_warranty ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid` LEFT JOIN cust_warranty_brokerage ON `cust_warranty_cost`.`id`=`cust_warranty_brokerage`.`cost_id`
`cust_warranty_cost`.`id`>#{page.lastId}
`cust_warranty_cost`.`id`>=(select `cust_warranty_cost`.`id` from cust_warranty_cost LEFT JOIN cust_warranty ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid` LEFT JOIN cust_warranty_brokerage ON `cust_warranty_cost`.`id`=`cust_warranty_brokerage`.`cost_id` where `cust_warranty`.`warranty_status`!=1 AND `cust_warranty`.`warranty_status`!=6 AND `cust_warranty`.`state` = 1 AND `cust_warranty_cost`.`actual_pay_time`<#{end_time} AND `cust_warranty_cost`.`actual_pay_time`>=#{start_time} AND `cust_warranty`.`manager_uuid`=#{manager_uuid}
and `warranty_code` like concat("%",#{search},"%")
ORDER BY `cust_warranty_cost`.`id` limit #{page.start},1 )
1=1
and `cust_warranty`.`warranty_status`!=1 AND `cust_warranty`.`warranty_status`!=6 AND `cust_warranty`.`state` = 1 AND `cust_warranty_cost`.`actual_pay_time`<#{end_time} AND `cust_warranty_cost`.`actual_pay_time`>=#{start_time}AND `cust_warranty`.`manager_uuid`=#{manager_uuid}
and `warranty_code` like concat("%",#{search},"%")
ORDER BY `cust_warranty_cost`.`id` limit #{page.offset}

select `cust_warranty`.`id` id,`cust_warranty`.`warranty_uuid` warranty_uuid,`pre_policy_no`,`cust_warranty`.`warranty_code`,`cust_warranty`.`manager_uuid`,`cust_warranty`.`account_uuid`,`cust_warranty`.`agent_id`,`cust_warranty`.`channel_id`,`cust_warranty`.`plan_id`,`cust_warranty`.`product_id`,`cust_warranty`.`start_time`,`cust_warranty`.`end_time`, `cust_warranty`.`ins_company_id`,`cust_warranty`.`count`,`cust_warranty`.`pay_category_id`,`cust_warranty`.`is_settlement`,`cust_warranty`.`warranty_url`,`cust_warranty`.`warranty_from`,`cust_warranty`.`type`,`cust_warranty`.`warranty_status`,`cust_warranty`.`integral`,`cust_warranty`.`express_no`,`cust_warranty`.`express_company_name`, `cust_warranty`.`express_address`,`cust_warranty`.`express_province_code`,`cust_warranty`.`express_city_code`,`cust_warranty`.`express_county_code`,`cust_warranty`.`delivery_type`, `cust_warranty`.`created_at` created_at,`cust_warranty`.`updated_at` updated_at, `cust_warranty_car`.`car_code` car_code,`cust_warranty_car`.`frame_no` frame_no, `cust_warranty_person`.`name` policy_holder_name,`cust_warranty_person`.`phone` policy_holder_phone, `cust_warranty_cost`.`premium` premium,`cust_warranty_cost`.`pay_status` pay_status,`cust_warranty_cost`.`pay_money` pay_money,`cust_warranty_cost`.`tax_money` tax_money, `cust_warranty_cost`.`actual_pay_time` actual_pay_time from cust_warranty_cost LEFT JOIN `cust_warranty_car` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid` LEFT JOIN `cust_warranty_person` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_person`.`warranty_uuid` AND `cust_warranty_person`.`type`=1 LEFT JOIN `cust_warranty` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid`
`cust_warranty_cost`.`id`<#{page.lastId}
`cust_warranty_cost`.`id`<=(select `cust_warranty_cost`.`id` from cust_warranty_cost LEFT JOIN `cust_warranty_car` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid` LEFT JOIN `cust_warranty_person` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_person`.`warranty_uuid` AND `cust_warranty_person`.`type`=1 LEFT JOIN `cust_warranty` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid` where `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1 AND `cust_warranty_cost`.`pay_status`= 206
AND `agent_id` in (${agent_id_string})
AND `cust_warranty_person`.`name` like concat("%",#{search},"%")
and `cust_warranty_car`.`car_code` like concat("%",#{search},"%")
and `warranty_code` like concat("%",#{search},"%")
and `cust_warranty`.`ins_company_id` in (${insurance_co_id_string})
and `cust_warranty`.`product_id`=#{type} in (${product_id_string})
and `cust_warranty`.`type`=#{type}
and `cust_warranty`.`created_at`>=#{start_time}
and `cust_warranty`.`created_at`<#{end_time}
and `cust_warranty`.`start_time`>=#{start_time}
and `cust_warranty`.`start_time`<#{end_time}
and `cust_warranty_cost`.`actual_pay_time`>=#{start_time}
and `cust_warranty_cost`.`actual_pay_time`<#{end_time}
ORDER BY `cust_warranty_cost`.`id` DESC limit #{page.start},1 )
1=1
and `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1 AND `cust_warranty_cost`.`pay_status`= 206
AND `agent_id` in (${agent_id_string})
AND `cust_warranty_person`.`name` like concat("%",#{search},"%")
AND `cust_warranty_car`.`car_code` like concat("%",#{search},"%")
and `warranty_code` like concat("%",#{search},"%")
and `cust_warranty`.`ins_company_id` in (${insurance_co_id_string})
and `cust_warranty`.`product_id`=#{type} in (${product_id_string})
and `cust_warranty`.`type`=#{type}
and `cust_warranty`.`created_at`>=#{start_time}
and `cust_warranty`.`created_at`<#{end_time}
and `cust_warranty`.`start_time`>=#{start_time}
and `cust_warranty`.`start_time`<#{end_time}
and `cust_warranty_cost`.`actual_pay_time`>=#{start_time}
and `cust_warranty_cost`.`actual_pay_time`<#{end_time}
ORDER BY `cust_warranty_cost`.`id` DESC limit #{page.offset}

select
COUNT(`cust_warranty_cost`.`id`)
from cust_warranty_cost
LEFT JOIN `cust_warranty_car` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid`
LEFT JOIN `cust_warranty_person` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_person`.`warranty_uuid`
AND
`cust_warranty_person`.`type`=1
LEFT JOIN `cust_warranty` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty`.`warranty_uuid`
where `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1
AND `cust_warranty_cost`.`pay_status`= 206



            AND `agent_id` in (${agent_id_string})


            AND `cust_warranty_person`.`name` like concat("%",#{search},"%")


            AND `cust_warranty_car`.`car_code` like concat("%",#{search},"%")


            AND `warranty_code` like concat("%",#{search},"%")




    and `cust_warranty`.`ins_company_id` in (${insurance_co_id_string})


    and `cust_warranty`.`product_id`=#{type} in (${product_id_string})


    and `cust_warranty`.`type`=#{type}





                and `cust_warranty`.`created_at`>=#{start_time}


                and `cust_warranty`.`created_at`<#{end_time}




                and `cust_warranty`.`start_time`>=#{start_time}


                and `cust_warranty`.`start_time`<#{end_time}




                and `cust_warranty_cost`.`actual_pay_time`>=#{start_time}


                and `cust_warranty_cost`.`actual_pay_time`<#{end_time}

select
COUNT(cust_warranty_cost.id)
from cust_warranty_cost
LEFT JOIN `cust_warranty` ON `cust_warranty`.`warranty_uuid`=`cust_warranty_cost`.`warranty_uuid` AND `cust_warranty`.`ins_company_id`=#{ins_company_id}
LEFT JOIN `cust_warranty_brokerage` ON `cust_warranty_cost`.`id`=`cust_warranty_brokerage`.`cost_id`
LEFT JOIN `cust_warranty_person` ON `cust_warranty_cost`.`warranty_uuid`=`cust_warranty_person`.`warranty_uuid` AND `cust_warranty_person`.`type`=2 AND `cust_warranty_person`.`record_end_time`=#{max_time}
WHERE `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state` = 1 AND `cust_warranty_cost`.`pay_status`= 206 AND `cust_warranty_cost`.`is_settlement`=0 AND (`cust_warranty_cost`.`bill_uuid` is NULL OR `cust_warranty_cost`.`bill_uuid`='')



            and `cust_warranty`.`warranty_code` like concat("%",#{search},"%")


            and `cust_warranty_person`.`name` like concat("%",#{search},"%")


            and 1=1






            and `cust_warranty_cost`.`actual_pay_time`<=#{start_time} AND `cust_warranty_cost`.`actual_pay_time`>#{end_time}


            and `cust_warranty`.`start_time`<=#{start_time} AND `cust_warranty`.`start_time`>#{end_time}


            and 1=1
