mapper.select:
        select

        from cust_warranty_claims



                    id>#{page.lastId}


                    id>=(select id from cust_warranty_claims where user_id=#{user_id} AND state=1

 --                       and `name` like concat("%",#{search},"%")



                        and status=#{status}

                    limit #{page.start},1
                    )


                    1=1


            and user_id=#{user_id} AND state = 1

--                 and `name` like concat("%",#{search},"%")



                and status=#{status}


            limit #{page.offset}
