select
count(id)
from cust_warranty

    manager_uuid=#{manager_uuid} AND state = 1

        and `warranty_code` like concat("%",#{search},"%")


        and `channel_id`=#{channel_id}


        and `start_time`>=#{start_time}


        and `start_time`<#{end_time}

select

from cust_warranty



            id<#{page.lastId}


            id<=(select id from cust_warranty where manager_uuid=#{manager_uuid} AND state=1

                and `start_time`>=#{start_time}


                and `start_time`<#{end_time}

            ORDER BY `cust_warranty`.`id` DESC limit #{page.start},1
            )


            1=1


    and `channel_id`=#{channel_id} AND `warranty_status`!= 1 AND `warranty_status`!= 6 AND state = 1

        and `start_time`>=#{start_time}


        and `start_time`<#{end_time}

    ORDER BY `cust_warranty`.`id` DESC limit #{page.offset}

select
COUNT(id)
from cust_warranty
WHERE `channel_id`=#{channel_id} AND `warranty_status`!= 1 AND `warranty_status`!= 6 AND state = 1

    and `start_time`>=#{start_time}


    and `start_time`<#{end_time}

select
count(id)
from cust_warranty

    state = 1

        and `account_uuid`=#{account_uuid}


        and `manager_uuid`=#{manager_uuid}


        and `start_time`>=#{start_time}


        and `start_time`<#{end_time}

select
`product_id`, count(id) product_count
from cust_warranty

    state = 1

        and `manager_uuid`=#{manager_uuid}


        and `start_time`>=#{start_time}


        and `start_time`<#{end_time}


        and `product_id` in (${product_id_string})


GROUP BY `product_id` ORDER BY `cust_warranty`.`id` DESC

