select
COUNT(id)
from cust_warranty
WHERE manager_uuid=#{manager_uuid} AND state = 1

    and `agent_id` in (${agent_id_string})


    and `type`=#{type}


    and `start_time`>=#{start_time}


    and `start_time`<#{end_time}


    and `warranty_status` in (${status_string})

select
`cust_warranty`.`id` id,`cust_warranty`.`warranty_uuid` warranty_uuid,`pre_policy_no`,`warranty_code`,`manager_uuid`,`account_uuid`,`agent_id`,`channel_id`,`plan_id`,`product_id`,`start_time`,`end_time`,
`ins_company_id`,`count`,`by_stages_way`,`is_settlement`,`warranty_url`,`warranty_from`,`type`,`warranty_status`,`integral`,`express_no`,`express_company_name`,
`express_address`,`express_province_code`,`express_city_code`,`express_county_code`,`delivery_type`,
`cust_warranty`.`created_at` created_at,`cust_warranty`.`updated_at` updated_at,
`biz_id`,`thp_biz_id`,`insurance_type`,`car_code`,`name`,`card_code`,`card_type`,`phone`,`birthday`,`sex`,`age`,
`frame_no`,`engine_no`,`vehicle_fgw_code`,`vehicle_fgw_name`,`brand_code`,`brand_name`,`parent_veh_name`,`engine_desc`,`is_not_car_code`,
`is_trans`,`trans_date`,`first_register_date`,`family_name`,`gearbox_type`,`car_remark`,`new_car_price`,`purchase_price_tax`,`import_flag`,`purchase_price`,
`car_seat`,`standard_name`,`coverage_list`,`sp_agreement`,`bj_code_flag`
from cust_warranty LEFT JOIN cust_warranty_car ON
`cust_warranty`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid`



            id<#{page.lastId}


            id<=(select `cust_warranty`.`id` from cust_warranty LEFT JOIN cust_warranty_car ON
            `cust_warranty`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid`
            where `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1

                and `cust_warranty_car`.`car_code` like concat("%",#{search},"%")


                and `cust_warranty`.`type`=#{type}


                and `cust_warranty`.`start_time`>=#{start_time}


                and `cust_warranty`.`start_time`<#{end_time}


                and `cust_warranty`.`warranty_status` in (${status_string})

            ORDER BY `cust_warranty`.`id` DESC limit #{page.start},1
            )


            1=1


    and `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1

        and `cust_warranty_car`.`car_code` like concat("%",#{search},"%")


        and `cust_warranty`.`type`=#{type}


        and `cust_warranty`.`start_time`>=#{start_time}


        and `cust_warranty`.`start_time`<#{end_time}


        and `cust_warranty`.`warranty_status` in (${status_string})

    ORDER BY `cust_warranty`.`id` DESC limit #{page.offset}

select
COUNT(`cust_warranty`.`id`)
from cust_warranty LEFT JOIN cust_warranty_car ON
`cust_warranty`.`warranty_uuid`=`cust_warranty_car`.`warranty_uuid`
WHERE `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1

    and `cust_warranty_car`.`car_code` like concat("%",#{search},"%")


    and `cust_warranty`.`type`=#{type}


    and `cust_warranty`.`start_time`>=#{start_time}


    and `cust_warranty`.`start_time`<#{end_time}


    and `cust_warranty`.`warranty_status` in (${status_string})
