  count(1)
from cust_warranty

    `warranty_uuid`=#{warranty_uuid} and agent_id = #{agent_id} and warranty_status=#{warranty_status}

        and start_time<= #{end_time}


        and end_time>=#{start_time}

     and state=1

select
  count(1)
from cust_warranty w
left join cust_warranty_cost c  on w.warranty_uuid=c.warranty_uuid and c.phase=1

    w.`warranty_uuid`=#{warranty_uuid} and w.agent_id = #{agent_id} and w.warranty_status=1 and w.state=1
    and c.pay_status=#{pay_status}

        and w.start_time<= #{end_time}


        and w.end_time>=#{start_time}

