            where `cust_warranty`.`manager_uuid`=#{manager_uuid} AND `cust_warranty`.`state`=1 AND `cust_warranty_person`.`record_start_time`<=#{currentTime} AND `cust_warranty_person`.`record_end_time`>#{currentTime}

                and `cust_warranty_person`.`name` like concat("%",#{search},"%")


                and `cust_warranty`.`type`=#{type}


                and `cust_warranty`.`created_at`>=#{start_time}


